(46, 11, 'CEPILLO IÓNICO DESENREDANTE HT 10', 'Para un cabello brillante y sedoso', '', '[\r\n    {\"color\":\"purple\", \r\n        \"foto\":\"https://beurer.pe/assets/sources/HT10_02.jpg\",\r\n        \"codigo\":\"ski0001\",\r\n        \"activo\":true},\r\n    {\"color\":\"#00aae4\",  \"foto\":\"https://beurer.pe/assets/sources/HT10_03.jpg\",\"codigo\":\"ski0002\",\"activo\":true}\r\n   \r\n]', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               